            $('.titulo-servicos').text('← Os nossos Serviços');
            $('.titulo-saber').text('← Quer saber mais?');
          }
        }

        $(window).on('resize', updateText);
        $(window).one('load', updateText); // Trigger the event handler once on page load
      });
    
    // overflow scroll da barra info
    function checkScreenWidth() {
      var screenWidth = window.innerWidth;

      if (screenWidth <= 850) {
        document.getElementById('infoBar').classList.add('overflow-x-scroll');
        document.getElementById('infoBar').classList.remove('justify-content-evenly');
      } else {
        document.getElementById('infoBar').classList.remove('overflow-x-scroll');
        document.getElementById('infoBar').classList.add('justify-content-evenly');
      }
    }
    
    window.addEventListener('resize', checkScreenWidth);
    checkScreenWidth(); // Initial check on page load
    </script>
  </body>
</html>